package com.net.rtsp.util;import java.security.BasicPermission;import java.security.Permission;/** * This permission grnts the right to create XEventQueue in a specific dispatch * group */public class DispatchGroupPermission extends BasicPermission {    /**     *      */    private static final long serialVersionUID = -3495047357804210641L;    /**     * Creates a new DispatchGroupPermission object for the specified group.     *      * @param groupName     *            group name     */    public DispatchGroupPermission(String groupName) {        super(groupName);    }    /**     * Checks two DispatchGroupPermission objects for equality. Checks that     * <i>other</i> is a DispatchGroupPermission, and has the same group name     * as this object.     *      * @param other     *            the object we are testing for equality with this object.     * @return <code>true</code> if <code>other</code> is of type     *         <code>DispatchGroupPermission</code> and has the same group     *         name as this <code>DispatchGroupPermission</code> object.     */    public boolean equals(Object other) {        if (this == other)            return true;        if (other instanceof DispatchGroupPermission) {            DispatchGroupPermission perm = (DispatchGroupPermission) other;            return getName().equals(perm.getName());        }        return false;    }    /**     * Returns the canonical string representation of the actions, which     * currently is the empty string "", since there are no actions for a     * DispatchGroupPermission.     *      * @return the empty string "".     */    public String getActions() {        return "";    }    /**     * Returns the hash code value for this object.     *      * @return A hash code value for this object.     */    public int hashCode() {        return getName().hashCode();    }    /**     * Checks if this DispatchGroupPermission object "implies" the specified     * permission.     * <p>     * More specificially, this method returns true if:     * <p>     * <ul>     * <li><i>p</i> is an instanceof DispatchGroupPermission, and     * <li><i>p</i>'s locator's external form is matches this object's locator     * string, or this object's locator string is "*".     * </ul>     *      * @param p     *            The permission to check against.     * @return <code>true</code> if the specified permission is implied by     *         this object, <code>false</code> if not.     */    public boolean implies(Permission p) {        if (!(p instanceof DispatchGroupPermission))            return false;        DispatchGroupPermission perm = (DispatchGroupPermission) p;        String name = getName();        if (name.equals("*"))            return true;        return equals(perm);    }}